import React, { Component } from 'react';

export default class App extends Component {
  state = {
    good: 0,
    neutral: 0,
    bad: 0
  }

  hendleClick (e){
    const name = e.target
    this.setState(state=>({[name] : state[name] +1}))


  }


}
