import React, { Component } from "react";

export default class App extends Component {
  state = {
    good: 0,
    neutral: 0,
    bad: 0,
  };

  increment = () => {
    const {name} = e.target;
    this.setState((prev)=>{
        return {[name]: + 1 }
    })
  }
  hendleClick(e) {
    const {name} = e.target;
    this.setState(prev => ({ [name]: prev.name + 1 }));
  }
  render() {
    // const { good, netural, bad } = this.state;
    return (
      <div className="container">
        <button
          className="button"
          type="button"
          name="good"
          onClick={this.increment}
        >
          good
        </button>
        <button
          className="button"
          type="button"
          name="netural"
          onClick={this.hendleClick}
        >
          netural
        </button>
        <button
          className="button"
          type="button"
          name="bad"
          onClick={this.hendleClick}
        >
          bad
        </button>
      </div>
    );
  }
}
