import React, { Component } from "react";

export default class App extends Component {
  state = {
    good: 0,
    neutral: 0,
    bad: 0,
  };


  handleClick = e => {
    const { name } = e.target;
    this.setState(state => ({ [name]: state[name] + 1 }));

  };
  render() {
    // const { good, netural, bad } = this.state;
    return (
      <h2 Please leave you feedback/>,
      <div className="container">
        <button
          className="button"
          type="button"
          name="good"
          onClick={this.handleClick}
        >
          good
        </button>
        <button
          className="button"
          type="button"
          name="neutral"
          onClick={this.handleClick}
        >
          neutral
        </button>
        <button
          className="button"
          type="button"
          name="bad"
          onClick={this.handleClick}
        >
          bad
        </button>
      </div>
    );
  }
}
