import React, { Component } from "react";

export default class App extends Component {
  state = {
    good: 0,
    neutral: 0,
    bad: 0,
  };
  countTotalFeedback = () => {
    const { good, neutral, bad } = this.state;
    return good + neutral + bad;
  };

  handleClick = (e) => {
    const { name } = e.target;
    this.setState((state) => ({ [name]: state[name] + 1 }));
    this.countTotalFeedback();
  };
  render() {
    const total = this.countTotalFeedback();
    // const { good, neutral, bad } = this.state;
    return (
      <div className="container">
        <h2>Please leave you feedback</h2>
        <button
          className="button"
          type="button"
          name="good"
          onClick={this.handleClick}
        >
          good
        </button>
        <button
          className="button"
          type="button"
          name="neutral"
          onClick={this.handleClick}
        >
          neutral
        </button>
        <button
          className="button"
          type="button"
          name="bad"
          onClick={this.handleClick}
        >
          bad
        </button>
        <p>Good:{good}</p>
        <p>Neutral:{neutral}</p>
        <p>Bad:{bad}</p>
        <p>Total:{total}</p>
      </div>
    );
  }
}
